<template>
  <div class="main-header">
    <i
      class="iconfont"
      :class="!isOpen ? 'icon-caidanxiangyou' : 'icon-caidanxiangzuo'"
      @click="changeMenu"
    ></i>
    <img :src="fullStationLogo" style="height: 1.1rem" />
    <div class="info-box" v-if="JSON.stringify(user_info) != '{}'">
      <div class="balance-box">
        <div class="unit">R$</div>
        <div class="amount">
          {{ user_balance.balance ? user_balance.balance : "0.00" }}
        </div>
        <i
          class="iconfont icon-shuaxin"
          @click="refreshBalance"
          :class="{ 'refresh-loading': isRefresh }"
        ></i>
        <div class="deposit-btn" @click="goPath('/deposit')">
          <svg
            t="1736711047466"
            class="icon"
            viewBox="0 0 1024 1024"
            version="1.1"
            xmlns="http://www.w3.org/2000/svg"
            p-id="1479"
            width="18"
            height="18"
          >
            <path
              d="M867.7 423.8H599.8V155.9c0-49.3-40-89.3-89.3-89.3s-89.3 40-89.3 89.3v267.9H153.3c-49.3 0-89.3 40-89.3 89.3s40 89.3 89.3 89.3h267.9v267.9c0 49.3 40 89.3 89.3 89.3s89.3-40 89.3-89.3V602.4h267.9c49.3 0 89.3-40 89.3-89.3s-40-89.3-89.3-89.3z"
              fill="#151515"
              p-id="1480"
            ></path>
          </svg>
        </div>
      </div>
      <div class="avatar-box" @click="changeShow">
        <div class="img-box">
          <div class="red"></div>
          <div class="level">{{ user_info.levelName }}</div>
          <img src="../assets/images/avatar/3.png" />
        </div>
        <i
          class="iconfont"
          :class="!isShow ? 'icon-xiangxiajiantou' : 'icon-xiangshangjiantou'"
        ></i>
      </div>
    </div>
    <div v-else class="info-box">
      <div class="login-btn" @click="goPermission('login')">
        {{ $t("button.login") }}
      </div>
      <div class="register-btn" @click="goPermission('register')">
        {{ $t("button.register") }}
      </div>
    </div>
  </div>
  <div class="content" id="content">
    <router-view v-slot="{ Component }">
      <keep-alive>
        <transition :name="transitionName" mode="out-in">
          <component
            :is="Component"
            v-if="route.meta.keepAlive"
            :key="route.name"
          ></component>
        </transition>
      </keep-alive>
      <transition :name="transitionName" mode="out-in">
        <component
          :is="Component"
          v-if="!route.meta.keepAlive"
          :key="route.name"
        ></component>
      </transition>
    </router-view>
  </div>

  <div class="tab-box">
    <div
      class="tab-item"
      v-for="(item, index) in tabs"
      :key="index"
      @click="tabSwitch(item.path)"
    >
      <nut-animate type="ripple" action="click">
        <div v-if="index != 2" class="common-tab">
          <i class="iconfont icon-rocket" v-if="item.path == '/home'"></i>
          <i class="iconfont icon-share" v-if="item.path == '/refer/invite'"></i>
          <i class="iconfont icon-lihe" v-if="item.path == '/promotion'"></i>
          <i class="iconfont icon-youjian" v-if="item.path == '/inbox'"></i>
          <i class="iconfont icon-gerenzhongxin" v-if="item.path == '/mine'"></i>
          <span>{{ item.name }}</span>
          <div class="msg-circle" v-if="item.path == '/inbox' && unread_count != 0">
            {{ unread_count }}
          </div>
        </div>
      </nut-animate>

      <div class="middle-tab" v-if="index == 2">
        <nut-animate type="breath" duration="800" loop>
          <div class="circle-box">
            <i class="iconfont icon-rocket" v-if="item.path == '/home'"></i>
            <i class="iconfont icon-share" v-if="item.path == '/refer/invite'"></i>
            <i class="iconfont icon-lihe" v-if="item.path == '/promotion'"></i>
            <i class="iconfont icon-youjian" v-if="item.path == '/inbox'"></i>
            <i class="iconfont icon-gerenzhongxin" v-if="item.path == '/mine'"></i>
          </div>
        </nut-animate>
        <span>{{ item.name }}</span>
      </div>
    </div>
  </div>

  <transition
    name="fade"
    enter-active-class="animate__animated animate__slideInDown"
    leave-active-class="animate__animated animate__slideOutUp"
  >
    <div v-if="isShow" class="info-pop-box">
      <div class="avatar-pop">
        <div class="avatar">
          <img src="../assets/images/avatar/3.png" />
          <div class="level">{{ user_info.levelName }}</div>
        </div>
        <div class="info-user">
          <div class="val" style="margin-bottom: 0.138rem">
            Account:{{ user_info.userName }}
          </div>
          <div class="val">
            ID:{{ user_info.userId }}
            <div class="copy-btn" @click="copyId">
              <svg
                t="1736770609856"
                class="icon"
                viewBox="0 0 1024 1024"
                version="1.1"
