<script>
import { computed, watch } from "vue";
import { onMounted, ref } from "vue";
import { useRouter, useRoute } from "vue-router";
// import { preloadImage } from "@/utils/utils";
import { useStore } from "vuex";
import { useI18n } from "vue-i18n";

export default {
  setup() {
    const { locale } = useI18n();
    let { state, commit, dispatch } = useStore();
    const promotion_list = computed(() => {
      return state.activity_notice.records;
    });
    const user_balance = computed(() => {
      return state.user_balance;
    });
    const fullStationLogo = computed(() => {
      return state.station_base.fullStationLogo;
    });
    const user_info = computed(() => {
      return state.user_info;
    });
    const tabs = computed(() => {
      return state.station_base.bottomTabBar;
      // const bottom = state.station_base.bottomTabBar;
      // bottom.map((item) => {
      //   item.icon = item.icon.replace('width="200"', 'width="0.666rem"');
      //   item.icon = item.icon.replace('height="200"', 'height="0.666rem"');
      //   item.icon = item.icon.replace('fill="#ffffff"', 'fill="#e556ff"');
      //   item.icon = item.icon.replace('fill="#3D3B4F"', 'fill="#e556ff"');
      //   item.icon = item.icon.replace('fill="#333333"', 'fill="#e556ff"');
      //   item.icon = item.icon.replace('fill="#333333"', 'fill="#e556ff"');
      //   item.icon = item.icon.replace(
      //     'xmlns="http://www.w3.org/2000/svg"',
      //     'xmlns="http://www.w3.org/2000/svg" fill="#e556ff"'
      //   );
      // });
      // return bottom;
    });
    const transitionName = ref("slide-right");
    const route = useRoute();
    const router = useRouter();
    const active_tab = ref(0);
    const isOpen = ref(false);
    const isShow = ref(false);
    // watch(
    //   () => route,
    //   (newRoute, oldRoute) => {
    //     switch (newRoute.name) {
    //       case "home":
    //         active_tab.value = 0;
    //         break;
    //       case "invite":
    //         active_tab.value = 1;
    //         break;
    //       case "promotion":
    //         active_tab.value = 2;
    //         break;
    //       case "inbox":
    //         active_tab.value = 3;
    //         break;
    //       case "mine":
    //         active_tab.value = 4;
    //         break;
    //     }
    //   },
    //   { deep: true, immediate: true }
    // );

    watch(
      () => route.meta,
      (newVal, oldVal) => {
        transitionName.value = newVal.transition;
      }
    );

    const tabSwitch = (path) => {
      router.push({
        path: path,
      });
    };

    const goPermission = (type) => {
      router.push({
        path: "/login",
        query: {
          mode: type,
        },
      });
    };

    const changeMenu = () => {
      isOpen.value = !isOpen.value;
    };

    const changeShow = () => {
      isShow.value = !isShow.value;
    };

    const goActive = (data) => {
      isOpen.value = false;
      commit("set_activity_detail", data);
      router.push({
        path: "/activity",
      });
    };

    const goPath = (path) => {
      isOpen.value = false;
      router.push({
        path,
      });
    };

    const refreshBalance = () => {
      if (isRefresh.value) return;
      isRefresh.value = true;
      setTimeout(() => {
        isRefresh.value = false;
      }, 3000);
      dispatch("GET_USER_BALANCE");
    };

    const languageChange = (value) => {
      locale.value = value;
    };

    const isRefresh = ref(false);

    // onMounted(() => {
    //   switch (route.name) {
    //     case "home":
    //       active_tab.value = 0;
    //       break;
    //     case "invite":
    //       active_tab.value = 1;
    //       break;
    //     case "promotion":
    //       active_tab.value = 2;
    //       break;
    //     case "inbox":
    //       active_tab.value = 3;
    //       break;
    //     case "mine":
    //       active_tab.value = 4;
    //       break;
    //   }
    // });

    const copyId = async () => {
      try {
        await navigator.clipboard.writeText(state.user_info.userId);
        console.log("ææ¬å·²å¤å¶å°åªè´´æ¿");
      } catch (err) {
        console.error("å¤å¶å°åªè´´æ¿å¤±è´¥", err);
      }
    };
    const popActionBtn = (path) => {
      isShow.value = false;
      router.push({
        path,
      });
    };
    const exit = () => {
      isShow.value = false;
      dispatch("LogOut");
    };
    const unread_count = computed(() => {
      return state.unread_count;
    });
    return {
      popActionBtn,
      exit,
      tabs,
      route,
      promotion_list,
      active_tab,
      tabSwitch,
      transitionName,
      fullStationLogo,
      state,
      goPermission,
      user_info,
      isOpen,
      changeMenu,
      isShow,
      changeShow,
      goActive,
      goPath,
      languageChange,
      locale,
      user_balance,
      refreshBalance,
      isRefresh,
      copyId,
      unread_count,
    };
  },
};
